import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ResultsPage.css';
import { Link } from 'react-router-dom';

const ResultsPage = () => {
  const [itemDetails, setItemDetails] = useState(null);
  // State to track if the user has rated. Expected format: { 'itemUrl1': true, 'itemUrl2': true, ... }
  const [hasRated, setHasRated] = useState({});
  const { id } = useParams();

  useEffect(() => {
    const fetchItemDetailsAndRatings = async () => {
      try {
        // Fetch item details
        const response = await axios.get(`http://127.0.0.1:5000/api/details/${id}`);
        setItemDetails(response.data);

        // Fetch the ratings
        const ratingsResponse = await axios.get(`http://127.0.0.1:5000/api/user-ratings`);
        // Convert the response to a map of itemUrl to boolean indicating if the user has rated
        const ratedItems = ratingsResponse.data.reduce((acc, { item_url }) => {
          acc[item_url] = true;
          return acc;
        }, {});
        setHasRated(ratedItems);
      } catch (error) {
        console.error('Failed to fetch item details or user ratings:', error);
      }
    };

    fetchItemDetailsAndRatings();
  }, [id]);

  const handleRating = async (itemUrl, rating) => {
    try {
      const response = await axios.post(`http://127.0.0.1:5000/api/rate`, {
        itemUrl,
        rating
      });
      // Check if the rating was successful and the response status is 200
      if (response.status === 200) {
        // Update hasRated state to indicate the item has been rated
        setHasRated(prevRatings => ({ ...prevRatings, [itemUrl]: true }));
      }
    } catch (error) {
      console.error('Rating failed:', error);
    }
  };

  if (!itemDetails) return <div className="loading">Loading...</div>;

  return (
    <div className="results-page-container">
      {/* Existing JSX markup */}
      <div className="right-section">
        {/* Existing JSX markup for detected items */}
        <h3>We Recommend:</h3>
        <div className="recommendations-container">
          {itemDetails.items.map((item, index) => (
            <div key={index} className="recommendation-item">
              {/* Existing JSX markup for recommendation item */}
              {/* Change the conditional rendering to check against the hasRated state */}
              {!hasRated[item.url] && (
                <div className="rating-buttons">
                  <button onClick={() => handleRating(item.url, 'like')} className="thumb-up">üëç</button>
                  <button onClick={() => handleRating(item.url, 'dislike')} className="thumb-down">üëé</button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ResultsPage;
