import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ResultsPage.css';

const ResultsPage = () => {
  const [itemDetails, setItemDetails] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/api/details/${id}`);
        setItemDetails(response.data);
      } catch (error) {
        console.error('Failed to fetch item details:', error);
      }
    };

    fetchItemDetails();
  }, [id]);

  if (!itemDetails) return <div>Loading...</div>;

  return (
    <div className="results-page">
      <h2>We Detected:</h2>
      {itemDetails && (
        <div className="item-details-container">
          <img src={`http://127.0.0.1:5000/images/${itemDetails.filename}`} alt="Detected Item" />
          <h3>Detected Items Timestamp: {itemDetails.timestamp}</h3>
          <div className="recommendations-container">
            <h4>We Recommend:</h4>
            {itemDetails.items.map((item, index) => (
              <p key={index}><a href={item.url}>{item.name}</a></p>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ResultsPage;
