import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ResultsPage.css'; // Ensure your CSS file is updated accordingly

const ResultsPage = () => {
  const [itemDetails, setItemDetails] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        // Fetch item details as before
        const response = await axios.get(`http://127.0.0.1:5000/api/details/${id}`);
        const itemDetailsData = response.data;

        // For each item, call the Flask API to get the Amazon products
        for (const item of itemDetailsData.items) {
          const amazonResponse = await axios.get(`http://127.0.0.1:5000/get_amazon_products`, {
            params: { search_term: item.name }
          });
          item.amazonDetails = amazonResponse.data; // Assume you want to attach the details here
        }

        setItemDetails(itemDetailsData);
      } catch (error) {
        console.error('Failed to fetch item details:', error);
      }
    };

    fetchItemDetails();
  }, [id]);

  if (!itemDetails) return <div className="loading">Loading...</div>;

  // Function to create rows of recommendations
  const recommendationRows = itemDetails.items.reduce((acc, item, index) => {
    const chunkIndex = Math.floor(index / 2);
    if (!acc[chunkIndex]) {
      acc[chunkIndex] = []; // start a new chunk
    }
    acc[chunkIndex].push(item);
    return acc;
  }, []);

  return (
    <div className="results-page-container">
      <div className="left-section">
        <h2>You Uploaded:</h2>
        <img src={`http://127.0.0.1:5000/images/${itemDetails.filename}`} alt="Uploaded Item" className="uploaded-item-image" />
      </div>
      <div className="right-section">
        <h2>We Detected:</h2>
        <div className="detected-items">
          <p>Detected Items Timestamp: {itemDetails.timestamp}</p>
          {/* Display other detected details as needed */}
        </div>
        <h3>We Recommend:</h3>
        <div className="recommendations-container">
          {recommendationRows.map((row, rowIndex) => (
            <div className="recommendation-row" key={rowIndex}>
              {row.map((item, itemIndex) => (
                <div className="recommendation-item" key={itemIndex}>
                  <a href={item.url}>{item.name}</a>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ResultsPage;
