import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ResultsPage.css';
import { Link } from 'react-router-dom';

const ResultsPage = () => {
  const [itemDetails, setItemDetails] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/api/details/${id}`);
        setItemDetails(response.data);
      } catch (error) {
        console.error('Failed to fetch item details:', error);
      }
    };

    fetchItemDetails();
  }, [id]);

  // Function to handle rating
  const handleRating = (itemUrl, rating) => {
    console.log("Sending rating data:", { itemUrl, rating });  // Log data being sent
    axios.post(`http://127.0.0.1:5000/api/rate`, { itemUrl, rating })
        .then(response => {
            console.log('Rating response:', response.data);
        })
        .catch(error => {
            console.error('Rating failed:', error);
            console.error('Data sent:', { itemUrl, rating }); // Re-log data on error
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                console.error('Error response:', error.response.data);
            }
        });
};




  if (!itemDetails) return <div className="loading">Loading...</div>;

  return (
    <div className="results-page-container">
      <header className="search-header">
        <h1>StyleSavvy</h1>
        <div className="search-nav">
          <Link to="/search">Search</Link>
          <Link to="/past-results">Past Results</Link>
          <Link to="/">Sign Out</Link>
        </div>
      </header>
      <div className="left-section">
        <h2>You Uploaded:</h2>
        <img src={`http://127.0.0.1:5000/images/${itemDetails.filename}`} alt="Uploaded Item" className="uploaded-item-image" />
      </div>
      <div className="right-section">
        <h2>We Detected:</h2>
        <div className="detected-items">
          <p>Detected Items: {itemDetails.timestamp}</p>
        </div>
        <h3>We Recommend:</h3>
        <div className="recommendations-container">
  {itemDetails.items.map((item, index) => (
    <div key={index} className="recommendation-item">
      <a href={item.url} target="_blank" rel="noopener noreferrer">{item.name}</a>
      <div className="rating-buttons">
        <button onClick={() => handleRating(item.url, 'up')} className="thumb-up">👍</button>
        <button onClick={() => handleRating(item.url, 'down')} className="thumb-down">👎</button>
      </div>
    </div>
  ))}
</div>

      </div>
    </div>
  );
};

export default ResultsPage;
