// ProfilePage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './ProfilePage.css'; // Ensure this points to the correct CSS file path

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    name: 'Jane Doe',
    age: '22',
    sex: 'Female',
  });

  // Instantiate navigate function
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleFileUpload = (e) => {
    // Placeholder function for file upload
    console.log('File uploaded:', e.target.files[0]);
    // You would typically also update the component's state to reflect the uploaded file here
  };

  const handleProfileUpdate = () => {
    // Placeholder function for updating profile
    console.log('Profile data:', profile);
    // Here you would typically send the updated profile data to your server
  };

  return (
    <div className="profile-page">
      <header>
        {/* Use navigate function to go back */}
        <button className="back-button" onClick={() => navigate(-1)}>Back</button>
        <h1>StyleSavvy</h1>
        <button className="edit-button" onClick={handleProfileUpdate}>Edit</button>
      </header>
      <h2 className="my-profile">My Profile</h2>
      <div className="profile-content">
        <section className="profile-picture-section">
          <div className="profile-picture">
            <img src="/path/to/default/profile_picture.png"/>
            <button className="upload-btn" onClick={() => document.getElementById('file-upload').click()}>
              Upload Picture
            </button>
            <input id="file-upload" type="file" onChange={handleFileUpload} />
          </div>
        </section>
        <section className="profile-info">
          <label htmlFor="name">
            Name:
            <input
              id="name"
              type="text"
              name="name"
              value={profile.name}
              onChange={handleInputChange}
            />
          </label>
          <label htmlFor="age">
            Age:
            <input
              id="age"
              type="number"
              name="age"
              value={profile.age}
              onChange={handleInputChange}
            />
          </label>
          <label htmlFor="sex">
            Sex:
            <select
              id="sex"
              name="sex"
              value={profile.sex}
              onChange={handleInputChange}
            >
              <option value="Female">Female</option>
              <option value="Male">Male</option>
              <option value="Other">Other</option>
            </select>
          </label>
        </section>
      </div>
    </div>
  );
};

export default ProfilePage;
