import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Signup() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();  // Initialize the useNavigate hook here

    const handleSignup = async () => {
        try {
            const response = await axios.post('http://127.0.0.1:5000/signup', { username, password });
            alert(response.data.message);
            navigate('/Login'); // Ensure the path is correct; it's case-sensitive and should match your Route definition
        } catch (error) {
            alert(error.response.data.message);
        }
    };

    return (
        <div>
            <h2>Signup</h2>
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
            <button onClick={handleSignup}>Signup</button>
        </div>
    );
}

export default Signup;
